import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function TodoPage() {
  const navigate = useNavigate();
  const [todo, setTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const [editMode, setEditMode] = useState(true);

  const todoSubmit = async () => {
    await axios.post(
      "http://localhost:8000/todos",
      { todo },
      {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("access_token")}`,
        },
      }
    );

    // axios
    //   .get("http://localhost:8000/todos", {
    //     headers: {
    //       authorization: `bearer ${localStorage.getItem("access_token")}`,
    //     },
    //   })
    //   .then((res) => setTodos(res.data));
  };

  useEffect(() => {
    if (!localStorage.getItem("access_token")) {
      navigate("/signin");
    }
  }, []);

  useEffect(() => {
    axios
      .get("http://localhost:8000/todos", {
        headers: {
          authorization: `bearer ${localStorage.getItem("access_token")}`,
        },
      })
      .then((res) => setTodos(res.data));
  }, [todos]);

  return (
    <div className="flex flex-col items-center mt-5">
      <div className="mb-2">
        <input
          data-testid="new-todo-input"
          className="w-56 mr-2 border border-neutral-400"
          onChange={(e) => setTodo(e.target.value)}
        />
        <button
          data-testid="new-todo-add-button"
          className="px-2 border bg-slate-300 border-neutral-400"
          onClick={todoSubmit}
        >
          추가
        </button>
      </div>
      <ul>
        {todos.map((todo) => (
          <li className="flex mb-2" key={todo.id}>
            <label className="flex mr-2">
              <input type="checkbox" className="mr-1" />
              {editMode ? <span>{todo.todo}</span> : null}
            </label>
            {editMode ? (
              <div>
                <button
                  data-testid="modify-button"
                  className="px-1 mr-1 border bg-slate-300 border-neutral-400"
                  onClick={() => setEditMode(false)}
                >
                  수정
                </button>
                <button
                  data-testid="delete-button"
                  className="px-1 border bg-slate-300 border-neutral-400"
                >
                  삭제
                </button>
              </div>
            ) : (
              <div>
                <input
                  data-testid="modify-input"
                  className="mr-2 border border-neutral-400 w-44"
                />
                <button
                  data-testid="submit-button"
                  className="px-1 mr-1 border bg-slate-300 border-neutral-400"
                >
                  제출
                </button>
                <button
                  data-testid="cancel-button"
                  className="px-1 border bg-slate-300 border-neutral-400"
                >
                  취소
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoPage;
